const results = [];

items.forEach(item => {
  const { city, country, languages, technology, synonyms } = item.json;

  const exclusions = '-wiki -pedia -tutorial -guide -course -bootcamp -training -documentation -repair -service -news -pdf -handbook -housing -rent -room -flat';

  const keywords = {
    en: {
      internship: '"internship" OR "trainee"',
      context: '("apply" OR "career" OR "job offer" OR "vacancy" OR "position")',
      duration: '("2 months" OR "8 weeks" OR "9 weeks" OR "10 weeks" OR summer OR july OR june OR august)',
      exclude: exclusions
    },
    fr: {
      internship: '"stage" OR "stagiaire"',
      context: '("postuler" OR "carri√®re" OR "offre d\'emploi" OR "recrutement" OR "poste")',
      duration: '("2 mois" OR "9 semaines" OR "10 semaines" OR √©t√© OR juillet OR ao√ªt OR juin)',
      exclude: exclusions.replace("-housing", "-logement -location") 
    },
    it: {
      internship: '"stage" OR "tirocinio"',
      context: '("candidati" OR "carriere" OR "lavoro" OR "posizioni aperte")',
      duration: '("2 mesi" OR "9 settimane" OR "10 settimane" OR estate OR luglio OR agosto OR giunio)',
      exclude: exclusions.replace("-housing", "-affitto -stanza -immobiliare")
    }
  };

  languages.forEach(lang => {
    const kw = keywords[lang];
    const mainQuery = `"${technology}" ${kw.internship} "${city}" ${kw.duration} ${kw.context} 2026 ${kw.exclude}`;

    results.push({
      json: {
        city,
        country,
        technology,
        language: lang,
        query: mainQuery,
        queryType: "general_web"
      }
    });

    if (synonyms && synonyms.length > 0) {
      const synList = `(${synonyms.join(' OR ')})`;
      const synQuery = `${synList} ${kw.internship} "${city}" ${kw.duration} ${kw.context} ${kw.exclude}`;

      results.push({
        json: {
          city, country, technology, language: lang,
          query: synQuery,
          queryType: "synonyms"
        }
      });
    }
  });
});

return results;

const cities = [
  { city: "Milan", country: "IT", languages: ["it", "en"] },
  { city: "Geneva", country: "CH", languages: ["fr", "en"] }
];

const techs = [
  { tech: "DevOps", synonyms: ["DevSecOps", "Platform Engineering"] },
  { tech: "Cloud", synonyms: ["AWS", "Azure", "GCP", "Kubernetes"] },
  //{ tech: "AI", synonyms: ["Machine Learning", "Deep Learning"] },
  //{ tech: "Quantum Computing", synonyms: ["Quantum", "Quantum AI"] }
];

// G√©n√©ration des paires ville + techno
return cities.flatMap(cityObj => 
  techs.map(techObj => ({
    city: cityObj.city,
    country: cityObj.country,
    languages: cityObj.languages,
    technology: techObj.tech,
    synonyms: techObj.synonyms
  }))
);

// On concat√®ne toutes les offres en un seul long texte
const reportData = items.map(item => {
  // --- CORRECTION ICI ---
  // On regarde si les donn√©es sont dans 'output' (nouveau format) ou √† la racine
  const i = item.json.output || item.json; 
  // ----------------------

  return `- [${i.verdict || "?"}] ${i.titre || "Sans titre"} chez ${i.entreprise || "?"} (${i.ville || "?"}, Score: ${i.score_ville || "?"}). Dur√©e: ${i.duree_texte || "?"}. Salaire: ${i.salaire || "?"}. Raison: ${i.raison_verdict || "?"}`;
}).join("\n");

return { json: { full_report_context: reportData, total_offers: items.length } };

