const newItems = [];

items.forEach(item => {
  const mcpData = item.json.content && item.json.content[0] ? item.json.content[0].text : {};
  const cityScore = mcpData.ranking || 50; 

  let targetCity = item.json.city || mcpData.city || "Unknown";

  if (targetCity === "Unknown" && item.json.query) {
      const matches = item.json.query.match(/"([^"]+)"/g);
      if (matches && matches.length >= 2) {
          targetCity = matches[1].replace(/"/g, ''); 
      }
  }

  const results = item.json.results || [];

  results.forEach(job => {
    newItems.push({
      json: {
        title: job.title,
        url: job.url,
        content: job.content,
        ranking: cityScore,
        city: targetCity
      }
    });
  });
});

return newItems;

const results = [];

items.forEach(item => {
  try {
    let rawContent = item.json.output || item.json.text || "";
    let parsedData = {};

    // 1. R√©cup√©ration ou Parsing des donn√©es
    if (typeof rawContent === 'object') {
      parsedData = rawContent;
    } else {
      const cleanJsonString = rawContent
        .replace(/```json/gi, '')
        .replace(/```/g, '')
        .trim();
      parsedData = JSON.parse(cleanJsonString);
    }

    // 2. üé® RECONSTRUCTION & MISE EN ORDRE
    // C'est ici qu'on d√©finit l'ordre exact des colonnes du CSV
    const orderedItem = {
      verdict: parsedData.verdict,          // On met le verdict en premier pour trier vite
      entreprise: parsedData.entreprise,
      titre: parsedData.titre,
      ville: parsedData.ville,
      score_ville: parsedData.score_ville,

      // On fusionne les skills pour √©viter skills.0, skills.1...
      // ["Java", "AWS"] devient "Java, AWS" dans une seule colonne
      skills: Array.isArray(parsedData.skills) ? parsedData.skills.join(', ') : parsedData.skills,

      salaire: parsedData.salaire,
      deadline: parsedData.deadline,
      duree_texte: parsedData.duree_texte,
      duree_semaines: parsedData.duree_semaines,
      url: parsedData.url,
      resume: parsedData.resume,
      raison_verdict: parsedData.raison_verdict
    };

    results.push({ json: orderedItem });

  } catch (error) {
    results.push({ 
      json: { 
        verdict: "ERREUR", 
        raison_verdict: "Erreur de format JSON",
        error_details: error.message
      } 
    });
  }
});

return results;

